import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { genreList } from "../../Components/genreList";

function GenreMovies() {
  let [movie, setMovie] = useState<any[]>([]);
  let ImgUrl = "https://image.tmdb.org/t/p/w500/";
  let name = useParams();

  let obj = genreList.genres.find((i) => {
    return i.name === `${name.Gname}`;
  });

  async function getGenreMovies() {
    let Api = await fetch(
      `https://api.themoviedb.org/3/discover/movie?api_key=${process.env.REACT_APP_TMBD_KEY}&include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc&with_genres=${obj?.id}`
    );

    let result = await Api.json();
    setMovie(result.results);
  }

  useEffect(() => {
    getGenreMovies();
  }, []);

  return (
    <div>
      <div className="container-fluid" style={{ maxWidth: "1500px" }}>
        <div className="grid-container">
          {movie?.map((data: any) => {
            return <MoviePoster key={data.id} {...data} />;
          })}
        </div>
      </div>
    </div>
  );
}

export default GenreMovies;
