import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { genreList } from "../../Components/genreList";

function GenreMovies() {
  const [movie, setMovie] = useState();

  const name = useParams();

  console.log(name.name);

  const obj = genreList.genres.find((i) => {
    return i.name === `${name.name}`;
  });

  async function getGenreMovies() {
    const Api = await fetch(
      `https://api.themoviedb.org/3/discover/movie?api_key=${process.env.REACT_APP_TMBD_KEY}&include_adult=true&include_video=false&language=en-US&page=1&sort_by=popularity.desc&with_genres=${obj?.id}`
    );
    const result = await Api.json();
    setMovie(result.results);
    console.log(result);
  }

  useEffect(() => {
    getGenreMovies();
  }, []);

  return <div>GenreMovies</div>;
}

export default GenreMovies;
