import React, { useEffect, useRef, useState } from "react";
import MoviePoster from "../../Components/MoviePoster";
import "../../Styling/home.css";
import { Swiper, SwiperSlide, SwiperRef } from "swiper/react";
import "swiper/css";

function Home() {
  const [movie, setMovie] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const API_Url = `https://api.themoviedb.org/3/discover/movie?api_key=${process.env.REACT_APP_TMBD_KEY}`;

  useEffect(() => {
    getMovies();
    setLoading(false);
  }, []);

  async function getMovies() {
    const api = await fetch(API_Url);
    const result = await api.json();
    setMovie(result.results);
    console.log(result.results);
  }

  if (loading) {
    return <div>Loading</div>;
  }

  return (
    <div className="container-fluid mt-5" style={{ maxWidth: "1500px" }}>
      <Swiper ref={SwiperRef} slidesPerView={4} spaceBetween={30}>
        {movie?.map((data: any) => {
          return (
            <SwiperSlide>
              {" "}
              <MoviePoster key={data.id} {...data} />
            </SwiperSlide>
          );
        })}
      </Swiper>
    </div>
  );
}

export default Home;
