import React, { useState } from "react";
import "../../Styling/account.css";
import { Link } from "react-router-dom";
import { useForm, FieldValues } from "react-hook-form";

interface FormData {
  firstname: string;
  lastname: string;
  date: string;
  email: string;
  password: string;
}

function SignUp() {
  const [user, setUser] = useState([]);

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm<FormData>();

  const onsubmit = (data: FieldValues) => {
    console.log(data);

    const newUser: FormData = {
      firstname: "",
      lastname: "",
      date: "",
      email: "",
      password: "",
    };

    setUser([newUser, ...user]);

    fetch("http://localhost:9000/", {
      method: "POST",
    }).then();

    reset();
  };

  return (
    <>
      <div className="backDesign">
        <div className="d-flex align-items-center justify-content-center h-100">
          <div className="box">
            <h2 className="signHead">SignUp</h2>
            <form
              className="d-flex flex-column"
              onSubmit={handleSubmit(onsubmit)}
            >
              <input
                {...register("firstname", {
                  required: "enter firstname",
                  minLength: {
                    value: 3,
                    message: "please enter more than 3 words",
                  },
                })}
                type="text"
                placeholder="Firstname"
                className="my-2"
              />
              <p className="text-danger">{errors.firstname?.message}</p>
              <input
                {...register("lastname", {
                  required: "enter Lastname",
                  minLength: {
                    value: 3,
                    message: "please enter more than 3 words",
                  },
                })}
                type="text"
                placeholder="Lastname"
                className="my-2"
              />
              <p className="text-danger">{errors.lastname?.message}</p>

              <input
                {...register("date", {
                  required: "select date",
                  // pattern: {
                  //   value:
                  //     /^(?:(?:1[6-9]|[2-9]\d)?\d{2})(\/|-|\.)(?:(?:0?[1-9])|(?:1[0-2]))\4(?:0?[1-9]|1\d|2[0-8])$/,
                  //   message: "please select correct format for date",
                  // },
                })}
                type="date"
                placeholder="Date of Birth"
                className="my-2"
              />
              <p className="text-danger">{errors.date?.message}</p>

              <input
                {...register("email", {
                  required: "Please enter the email...",
                  pattern: {
                    value:
                      /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/,
                    message: "please follow correct format for email...",
                  },
                })}
                type="email"
                placeholder="Email"
                className="my-2"
              />
              <p className="text-danger">{errors.email?.message}</p>

              <input
                {...register("password", {
                  required: "please enter correct password...",
                  minLength: {
                    value: 6,
                    message: "Must be greater than 6 characters...",
                  },
                  pattern: {
                    value:
                      /^(?=.*[0-9])(?=.*[!@#$%^&*.,])[a-zA-Z0-9!@#$%^&*.,]{6,16}$/,
                    message:
                      "please password must contain 6 charachter in which one uppercase, one lowercase, one number and one spacial character...",
                  },
                })}
                type="password"
                placeholder="Passowrd"
                className="my-2"
              />
              <p className="text-danger">{errors.password?.message}</p>

              {/* <Link to="/account/login" className="w-100"> */}
              <button className="signBtn w-100" type="submit">
                Sign Up
              </button>
              {/* </Link> */}
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default SignUp;
